const breadcrumbLinks = [
  { href: "/", children: "Home" },
  { href: "/products", children: "Products" },
  { href: "/products/category", children: "Category" },
];
const handleLinkClick = () => {
  console.log("Breadcrumb link clicked!");
};
<Breadcrumb
  links={[
    { href: "/", children: "Home" },
    {
      href: "/products",
      children: "Products",
      onClick: handleLinkClick,
    },
    { href: "/products/category", children: "Category" },
  ]}
/>;

